import axios from "axios";
import { useEffect, useState } from "react";
import { Inner } from "../Create";

export function Log() {
  const [getdata, setgetdata] = useState([]);
  const [valid, setvalid] = useState(false);
  const [email, setemail] = useState("");
  const [password, setpassword] = useState("");
  const [user, setUser] = useState(null);
  const style1 = {
    marginTop: "30px",
  };

  const handleOnLogin = (e) => {
    e.preventDefault();
    for (let i = 0; i < getdata.length; i++) {
      if (getdata[i].email === email && getdata[i].password === password) {
        alert("Logged in");
        setvalid(true);
        setUser(getdata[i].org);
        chan(getdata[i].org);
        return;
      }
    }
    valid ? alert("logged in") : alert("Password or email is wrong");
  };


  function getdatafun() {
    axios.get("https://localhost:5000/login").then((res) => {
      setgetdata(res.data);
      console.log(getdata);
    });
  }
  useEffect(() => {
    getdatafun();
    axios.get("https://localhost:5000/login").then((res) => {
      setUser(res.data.name);
      if (user !== null) {
        setvalid(true);
      }
    });

  }, [user]);

  const chan = (a) => {
    let n = { name: a };
    axios
      .patch("https://localhost:5000/", n)
      .then((res) => {
      });
  };

  const logOut = () => {
    let x = window.confirm("You want to Log-Out");
    if (x) {
      let n = { name: null };
      axios
        .patch("https://localhost:5000/login", n)
        .then((res) => {
        });
    }
  };

  return user !== null && valid ? (
    <div>
      <Inner>
        <button onClick={logOut}>Log Out</button>
      </Inner>
    </div>
  ) : (
    <form onSubmit={handleOnLogin}>
      <Inner>
        <input
          style={style1}
          type="text"
          onChange={(e) => setemail(e.target.value)}
          placeholder="Email"
        />

        <input
          style={style1}
          type="password"
          onChange={(e) => setpassword(e.target.value)}
          placeholder="Password"
        />

        <input style={style1} type="submit" value="Log-In" />
      </Inner>
    </form>
  );
}
