import styled from "styled-components";
import Modal from "react-modal";
import { useState } from "react";
import axios from "axios";
const CardWrap = styled.div`
  font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
  width: 300px;
  height: 400px;
  margin: 15px;
  display: flex;
  flex-direction: column;
  text-align: left;
  justify-content: space-between;
  /* padding: 15px; */
  border: 1px solid rgb(216, 230, 239);
  h4 {
    font-size: 16px;
    padding: 0 30px;
    margin: 0;
  }
  p {
    padding: 0 30px;
    font-size: 14px;
  }
  img {
    width: 100%;
    height: 35%;
  }
  div {
    display: flex;
    text-align: center;
    justify-content: space-around;
  }
`;
const Bot = styled.div`
  background-color: rgb(213, 234, 243);

  h5 {
    padding-top: 15px;
    margin: 0;
  }
  div {
    display: flex;
    flex-direction: column;
  }
`;
const InMod = styled.div`
  text-align: center;
`;
const Cont = styled.div`
  width: 90%;
  margin: auto;
  display: flex;
  justify-content: space-between;
  img {
    width: 65%;
    height: auto;
  }
  div {
    width: 25%;
    padding: 0 30px;
    text-align: center;
  }
  div > div {
    width: 100%;
    display: flex;
    justify-content: space-between;
  }
`;
Modal.defaultStyles.content.width = "1000px";

export default function CardExp({ project }) {
  const [open, setOpen] = useState(false);
  const [don, setDon] = useState(0);
  // const [project, setproject] = useState(p);

  const handleDonate = () => {
    let x = window.confirm("Confirm Payment");
    if (!x) {
      return;
    }
    if (project.donated === undefined) {
      project.donated = Number(don);
    } else {
      project.donated = Number(project.donated) + Number(don);
    }
    if (project.backers === undefined) {
      project.backers = Number(1);
    } else {
      project.backers = Number(project.backers) + 1;
