import axios from "axios";
import { useEffect, useState } from "react";
import { Inner } from "../Create";
export function Sign() {
  const [user, setuser] = useState({
    firstnameReg: "",
    firstnameReg: "",
    emailReg: "",
    passwordReg: "",
  });

  const style2 = {
    marginTop: "22px",
  };
  const handleOnChange = (e) => {
    let data = {
      ...user,
      [e.target.name]: e.target.value,
    };
    setuser(data);
  };
  const handleOnSubmit = (e) => {
    e.preventDefault();
    console.log(user);
    if (
      user.firstname === "" ||
      user.lastname === "" ||
      user.password === "" ||
      user.email === ""
    ) {
      alert("you have to fil every section");
      return;
    }
    axios
      .post("http://localhost:3000/Sign", {
        firstname:firstnameReg,
        lastname:lastnameReg,
        email:emailReg,
        password:passwordReg,

      })
      .then((res) => {
        console.log(res);
        alert("Account Created Succesfully, Please Log-In to Continue");
      })
      .catch((e) => {
        console.log(e);
      });
  };
  return (
    <>
      <form onSubmit={handleOnSubmit}>
        <Inner>
          <input
            style={style2}
            onChange={handleOnChange}
            type="text"
            placeholder="First Name"
            name="firstname"
          />
          <input
            style={style2}
            onChange={handleOnChange}
            type="text"
            placeholder="Last Name"
            name="lastname"
          />

          <input
            style={style2}
            onChange={handleOnChange}
            type="email"
            placeholder="Email"
            name="email"
          />

          <input
            style={style2}
            onChange={handleOnChange}
            type="password"
            placeholder="Passsword"
            name="password"
          />
          <input style={style2} type="submit" value="Sign-Up" />
        </Inner>
      </form>
    </>
  );
}
