import axios from "axios";
import { useEffect, useState } from "react";
import { Inner } from "../Create";
export function Sign() {
  const [user, setuser] = useState({
    org: "",
    email: "",
    num: "",
    password: "",
  });

  const style2 = {
    marginTop: "25px",
  };
  const handleOnChange = (e) => {
    let data = {
      ...user,
      [e.target.name]: e.target.value,
    };
    setuser(data);
  };
  const handleOnSubmit = (e) => {
    e.preventDefault();
    console.log(user);
    if (
      user.org === "" ||
      user.num === "" ||
      user.password === "" ||
      user.email === ""
    ) {
      alert("you have to fil every section");
      return;
    }
    axios
      .post("http://localhost:3000/Sign", user)
      .then((e) => {
        console.log(e);
        alert("Account Created Succesfully, Please Log-In to Continue");
      })
      .catch((e) => {
        console.log(e);
      });
  };
  // useEffect(()=>{

  // },[user])
  return (
    <>
      <form onSubmit={handleOnSubmit}>
        <Inner>
          <input
            style={style2}
            onChange={handleOnChange}
            type="text"
            placeholder="First Name"
            name="fname"
          />
          <input
            style={style2}
            onChange={handleOnChange}
            type="text"
            placeholder="Last Name"
            name="lname"
          />

          <input
            style={style2}
            onChange={handleOnChange}
            type="email"
            placeholder="Email"
            name="email"
          />

          <input
            style={style2}
            onChange={handleOnChange}
            type="password"
            placeholder="Passsword"
            name="password"
          />

          <input
            style={style2}
            onChange={handleOnChange}
            type="text"
            placeholder="Country"
            name="country"
          />
          <input style={style2} type="submit" value="Sign-Up" />
        </Inner>
      </form>
    </>
  );
}
