import React from "react";
import "./Form.css";
//import CountryCity from "./CountryCity";
import useForm from "./useForm";
import validate from "./ValidateInfo";
import { Inner } from "../Inner";

function Form() {
  // const { handleChange, inputs, handleSubmit, errors } = useForm(validate);
  const [errors, setErrors] = useState({});

  const handleSubmit = (e) => {
    e.preventDefault();
    setErrors(validate(inputs));

    const [participantDetails, setparticipantDetails] = useState("");

  }
  
  const handleChange = e => {
    setInputs({
      ...inputs,
      [e.target.name]: e.target.value
    });
  };
  return (
    <div className="form">
      <form onSubmit={handleSubmit}>
        <Inner>

          <div className="form__row">
            <h3>PARTICIPANT DETAILS</h3>
            <input
              type="text"
              name="participantRole"
              placeholder="Pariticipant Role in Organization"
              value={inputs.organizationname}
              onChange={handleChange}
              className={`${errors.organizationname} ? "error" : "success"`}
              onBlur={handleSubmit}
              autoComplete="off"
            />
            <h3>ORGANIZATION DETAILS</h3>
            <input
              type="text"
              name="organizationnmae"
              placeholder="Organization Name (in english)*"
              value={inputs.organizationname}
              onChange={handleChange}
              className={`${errors.organizationname} ? "error" : "success"`}
              onBlur={handleSubmit}
              autoComplete="off"
            />



            <input
              type="text"
              name="organizationdetails"
              placeholder="Organization Details *"
              value={inputs.jobtitle}
              onChange={handleChange}
              className={`${errors.organizationdetails} ? "error" : "success"`}
              onBlur={handleSubmit}
              autoComplete="off"
            />

            <h3>  PROJECT-DETAILS</h3>
            <input
              type="text"
              name="projectname"
              placeholder="Project Name *"
              value={inputs.projectname}
              onChange={handleChange}
              className={`${errors.projectname} ? "error" : "success"`}
              onBlur={handleSubmit}
              autoComplete="off"
            />


            <input
              type="text"
              name="projectdescription"
              placeholder="Project Description *"
              value={inputs.projectdescription}
              onChange={handleChange}
              className={`${errors.projectdescription} ? "error" : "success"`}
              onBlur={handleSubmit}
              autoComplete="off"
            />

            <input
              type="text"
              name="country"
              placeholder="Country *"
              value={inputs.country}
              onChange={handleChange}
              className={`${errors.country} ? "error" : "success"`}
              onBlur={handleSubmit}
              autoComplete="off"
            />
            <input
              type="text"
              name="city"
              placeholder="City *"
              value={inputs.city}
              onChange={handleChange}
              className={`${errors.city} ? "error" : "success"`}
              onBlur={handleSubmit}
              autoComplete="off"
            />


            <input
              type="number"
              name="amountrequired"
              placeholder="Amount Required"
              value={inputs.amountrequired}
              onChange={handleChange}
              className={`${errors.amountrequired} ? "error" : "success"`}
              onBlur={handleSubmit}
              autoComplete="off"
            />
            <h4>Start Date</h4>
            <input
              type="date"
              name="startdate"
              placeholder="Start Date"
              value={inputs.startdate}
              onChange={handleChange}
              className={`${errors.startdate} ? "error" : "success"`}
              onBlur={handleSubmit}
              autoComplete="off"
            />
            <h4>End Date</h4>
            <input
              type="date"
              name="enddate"
              placeholder="End Date"
              value={inputs.enddate}
              onChange={handleChange}
              className={`${errors.enddate} ? "error" : "success"`}
              onBlur={handleSubmit}
              autoComplete="off"
            />
            <h4>SUPPORTING-DOCUMENT</h4>
            <input
              type="file"
              name="document"
              placeholder="UPLOAD"
              value={inputs.document}
              onChange={handleChange}
              className={`${errors.document} ? "error" : "success"`}
              onBlur={handleSubmit}
              autoComplete="off"
            />



            <button>SUBMIT</button>
          </div>
        </Inner>
      </form>
    </div>
  );
}

export default Form;
