import styled from "styled-components";
import Modal from "react-modal";
import { useState } from "react";
import axios from "axios";
const CardWrap = styled.div`
  font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
  width: 300px;
  height: 400px;
  margin: 15px;
  display: flex;
  flex-direction: column;
  text-align: left;
  justify-content: space-between;
  /* padding: 15px; */
  border: 1px solid rgb(216, 230, 239);
  h4 {
    font-size: 16px;
    padding: 0 30px;
    margin: 0;
  }
  p {
    padding: 0 30px;
    font-size: 14px;
  }
  img {
    width: 100%;
    height: 35%;
  }
  div {
    display: flex;
    text-align: center;
    justify-content: space-around;
  }
`;
const Bot = styled.div`
  background-color: rgb(213, 234, 243);

  h5 {
    padding-top: 15px;
    margin: 0;
  }
  div {
    display: flex;
    flex-direction: column;
  }
`;
const InMod = styled.div`
  text-align: center;
`;
const Cont = styled.div`
  width: 90%;
  margin: auto;
  display: flex;
  justify-content: space-between;
  img {
    width: 65%;
    height: auto;
  }
  div {
    width: 25%;
    padding: 0 30px;
    text-align: center;
  }
  div > div {
    width: 100%;
    display: flex;
    justify-content: space-between;
  }
`;
Modal.defaultStyles.content.width = "1000px";

export default function CardExp({ project }) {
  const [open, setOpen] = useState(false);
  const [donation, setDonation] = useState(0);

  const handleDonate = () => {
    let x = window.confirm("Confirm Payment");
    if (!x) {
      return;
    }
      project.pledged = Number(project.pledged) + Number(donation);
      project.investors = Number(project.investors) + 1;

    let insert = { donated: Number(project.pledged), backers: Number(project.investors) };
    axios.patch(`http://localhost:3001/projects/${project.id}`, insert)
      .then((res) => {
      });
  };
  return (
    <div onClick={() => setOpen(true)}>
      <CardWrap>
        <img
          src="https://www.catapooolt.com/assets/images/c-logo.png"
          alt="Logo"
          onClick={() => setOpen(true)}
        />

        <h4>{project.project_name}</h4>
        <h5>Start Date: {project.start_date}</h5>
        <h5>End Date  : {project.end_date}</h5>
        <Bot>
          <div>
            <h5>Goal:{project.goal}</h5>
          </div>
          <div>
            <h5>Funded:{project.pledged}</h5>
          </div>
          <div>
            <h5>Investors:{project.investors}</h5>
          </div>
        </Bot>
      </CardWrap>
      <Modal
        isOpen={open}
        onRequestClose={() => {
          setOpen(false);
        }}
      >
        <InMod>
          <h1>{project.title}</h1>
          <Cont>
            <img
              src= "https://www.catapooolt.com/assets/images/c-logo.png" alt="LOGO"/>
            <div>
              <p>
                {project.donated
                  ? (Number(project.pledged) / Number(project.goal)).toFixed(2) * 100
                  : 0}
                % Completed
              </p>
              <h4>PKR{project.goal}</h4>
              <div>
                <p>Funded</p>
                <p>{project.pledged ? project.pledged : "No donations Yet"}</p>
              </div>
              <div>
                <p>Investors</p>
                <p>{project.investors ? project.investors : "No Investors Yet"}</p>
              </div>
              <input
                type="Number"
                placeholder="Contribute"
                name="donated"
                onChange={(e) => {
                  setDonation(e.target.value);
                }}
              />
              <button onClick={handleDonate}>DONATE</button>
            </div>
          </Cont>
        </InMod>
      </Modal>
    </div>
  );
}
